function noEffect(a,b){if(a){a.style.display="none"}if(b){b.style.display="block"}}function crossFade(b,c,a){if(c){new Effect.Appear(c.id,{duration:a})}if(b){new Effect.Fade(b.id,{duration:a})}}function blindDown(b,c,a){if(c){c.style.zIndex="2"}if(b){b.style.zIndex="1"}if(c){new Effect.BlindDown(c.id,{duration:a})}if(b){new Effect.Fade(b.id,{duration:a})}}function blindUp(b,c,a){if(c){c.style.zIndex="1"}if(b){b.style.zIndex="2"}if(c){new Effect.Appear(c.id,{duration:a})}if(b){new Effect.BlindUp(b.id,{duration:a})}}function InteraktingSlider(){this.slides=new Array();this.delay=5;this.chrono=0;this.currentSlide=0;this.previousSlide=null;this.mode="mixte";this.transition="none";this.speed=1;this.height=100;this.width=200;return this}InteraktingSlider.prototype.setDelay=function(a){if(a>0){this.delay=a}};InteraktingSlider.prototype.setMode=function(a){this.mode=a};InteraktingSlider.prototype.isAutoMode=function(){if(this.mode!="manual"){return true}return false};InteraktingSlider.prototype.isManualMode=function(){if(this.countSlides()<1){return false}if(this.mode!="auto"){return true}return false};InteraktingSlider.prototype.setChrono=function(a){this.chrono=a};InteraktingSlider.prototype.setTransition=function(a){this.transition=a};InteraktingSlider.prototype.setSpeed=function(a){if(a>0){this.speed=a/1000}};InteraktingSlider.prototype.addSlide=function(b){var d=document.getElementById("slide-content");if(d){var a=this.countSlides();var c="<div id='slide-"+a+"' class='slide' style='display:none;position:absolute;'>"+b+"</div>";d.innerHTML+=c}this.slides.push(a)};InteraktingSlider.prototype.countSlides=function(){return this.slides.length};InteraktingSlider.prototype.getCurrentSlide=function(){return this.slides[this.currentSlide]};InteraktingSlider.prototype.setCurrentSlide=function(a){this.previousSlide=this.currentSlide;this.currentSlide=a};InteraktingSlider.prototype.isCurrentSlide=function(a){return(this.currentSlide==a)};InteraktingSlider.prototype.setCurrentSlideUp=function(){this.previousSlide=this.currentSlide;this.currentSlide++};InteraktingSlider.prototype.setCurrentSlideDown=function(){this.previousSlide=this.currentSlide;this.currentSlide--};InteraktingSlider.prototype.turnLeft=function(){var a=this.countSlides();if(this.currentSlide==0){this.goTo(a-1)}else{this.setCurrentSlideDown()}this.refresh()};InteraktingSlider.prototype.turnRight=function(){var a=this.countSlides();if(this.currentSlide==(a-1)){this.goTo(0)}else{this.setCurrentSlideUp()}this.refresh()};InteraktingSlider.prototype.goTo=function(a){if(a!=this.currentSlide){this.setCurrentSlide(a);this.refresh()}};InteraktingSlider.prototype.getCommande=function(){var a="";a+="<a href='#' id='prev' class='normal' onclick='interaktingslider.turnLeft();return false;'></a>";for(i=0;i<this.countSlides();i++){a+="<a href='#' id='button"+i+"' onclick='interaktingslider.goTo("+i+");return false;'></a>"}a+="<a href='#' id='next' class='normal' onclick='interaktingslider.turnRight();return false;'></a>";return a};InteraktingSlider.prototype.setCurrentButton=function(){for(i=0;i<this.countSlides();i++){var a=document.getElementById("button"+i);if(this.isCurrentSlide(i)){a.setAttribute("class","active");a.setAttribute("className","active")}else{a.setAttribute("class","normal");a.setAttribute("className","normal")}}};InteraktingSlider.prototype.show=function(){if(this.isManualMode()){div=document.getElementById("middle-center");if(div){div.innerHTML+='<div id="slide-commands"><div class="nav-intertaking">'+this.getCommande()+'</div><div class="clear"></div></div>'}}if(this.countSlides()>0){this.refresh();this.run()}};InteraktingSlider.prototype.changeSlide=function(){if(this.isManualMode()){this.setCurrentButton()}if(this.transition=="random"){this.makeTransition(this.getRandomTransition())}else{this.makeTransition(this.transition)}};InteraktingSlider.prototype.makeTransition=function(b){var c=document.getElementById("slide-"+this.previousSlide);var a=document.getElementById("slide-"+this.currentSlide);switch(b){case"up":blindUp(c,a,this.speed);break;case"down":blindDown(c,a,this.speed);break;case"fade":crossFade(c,a,this.speed);break;case"none":default:noEffect(c,a,this.speed)}};InteraktingSlider.prototype.refresh=function(){this.changeSlide();this.setChrono(this.delay)};InteraktingSlider.prototype.run=function(){if(this.chrono==0){this.turnRight()}else{this.chrono--}if(this.isAutoMode()){setTimeout("interaktingslider.run()",1000)}};